import React from "react";
import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import Slideshow from "./components/Slideshow";
import Courses from "./components/Courses";

// ✅ Chatbot component defined only once
function Chatbot() {
  const [messages, setMessages] = React.useState([
    { from: "bot", text: "Hi! Ask your school question." },
  ]);
  const [input, setInput] = React.useState("");

  const sendMessage = () => {
    if (input.trim() === "") return;
    setMessages([
      ...messages,
      { from: "user", text: input },
      {
        from: "bot",
        text: "Thanks for your question! We'll get back to you soon.",
      },
    ]);
    setInput("");
  };

  return (
    <div
      style={{
        position: "fixed",
        bottom: "10px",
        right: "10px",
        border: "1px solid #ccc",
        padding: "10px",
        background: "white",
        width: "250px",
      }}
    >
      <div
        style={{
          maxHeight: "150px",
          overflowY: "auto",
          marginBottom: "5px",
          fontSize: "14px",
        }}
      >
        {messages.map((msg, index) => (
          <div key={index}>
            <b>{msg.from}:</b> {msg.text}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Type a message..."
        style={{ width: "70%", marginRight: "5px" }}
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
}

function App() {
  return (
    <div>
      <Navbar />
      <Slideshow />
      <div style={{ padding: "20px" }}>
        <h2>Our Vision/Mission/Moto</h2>
        <p>Empowering rural students with accessible education.</p>

        <h2>Popular Courses</h2>
        <Courses />

        <h2>Why Choose Us?</h2>
        <p>Quality education, offline access, multi-language support.</p>

        <h2>Contact Us</h2>
        <p>Email: info@example.com</p>

        <h2>FAQs</h2>
        <p>Coming soon!</p>
      </div>
      <Footer />
      {/* ✅ Chatbot added once */}
      <Chatbot />
    </div>
  );
}

export default App;
